<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Turno;
use AppBundle\Entity\User;
use Doctrine\ORM\EntityRepository;

/**
 * TurnoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TurnoRepository extends EntityRepository
{
    /**
     * @param User $user
     * @return array
     */
    public function findByNotUser(User $user)
    {
        $qb = $this->createQueryBuilder('t');
        $qb->from('AppBundle:Turno', 'u')
            ->where('t.user != :user')
            ->setParameter('user', $user);
        return $qb->getQuery()->getResult();
    }

    /**
     * @param Turno $turno
     * @return array
     */
    public function findByUserAndDate(Turno $turno)
    {
        $qb = $this->createQueryBuilder('at');
        $qb->from(Turno::class, 't')
            ->where("t.fecha like :fecha")
            ->setParameter('fecha', $turno->getFecha()->format('Y-m-d') . '%');
        return $qb->getQuery()->getArrayResult();
    }
}
